// SPDX-License-Identifier: GPL-2.0-or-later

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable, Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {Currency, CurrencyLibrary} from "clober-dex/v2-core/libraries/Currency.sol";

import "./interfaces/ISimpleOracleStrategy.sol";
import "./interfaces/IRebalancer.sol";
import {IDatastreamOracle} from "./interfaces/IDatastreamOracle.sol";

contract Operator is UUPSUpgradeable, Initializable, Ownable2Step {
    using CurrencyLibrary for Currency;

    IRebalancer public immutable rebalancer;
    IDatastreamOracle public immutable datastreamOracle;

    constructor(IRebalancer rebalancer_, IDatastreamOracle datastreamOracle_) Ownable(msg.sender) {
        rebalancer = rebalancer_;
        datastreamOracle = datastreamOracle_;
    }

    function initialize(address initialOwner) external initializer {
        _transferOwnership(initialOwner);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    function updatePosition(bytes32 key, uint256 oraclePrice, Tick tickA, Tick tickB, uint24 rate) external onlyOwner {
        ISimpleOracleStrategy oracleStrategy = ISimpleOracleStrategy(address(rebalancer.getPool(key).strategy));
        if (oracleStrategy.isPaused(key)) {
            oracleStrategy.unpause(key);
        }
        oracleStrategy.updatePosition(key, oraclePrice, tickA, tickB, rate);
        rebalancer.rebalance(key);
    }

    function pause(bytes32 key) external onlyOwner {
        ISimpleOracleStrategy(address(rebalancer.getPool(key).strategy)).pause(key);
        rebalancer.rebalance(key);
    }

    function requestOraclePublic() external {
        address feeToken = datastreamOracle.feeToken();
        IERC20(feeToken).transferFrom(msg.sender, address(this), (10 ** IERC20Metadata(feeToken).decimals()) / 20);
        datastreamOracle.request(type(uint256).max);
    }

    function requestOracle(uint256 bitmap) external onlyOwner {
        datastreamOracle.request(bitmap);
    }

    function withdraw(Currency currency, address to, uint256 amount) external onlyOwner {
        currency.transfer(to, amount);
    }
}

src/oracle/ChainlinkOracle.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later

pragma solidity ^0.8.0;

import {Ownable, Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";

import {AggregatorV3Interface} from "../external/chainlink/AggregatorV3Interface.sol";
import {IOracle} from "../interfaces/IOracle.sol";
import {IChainlinkOracle} from "../interfaces/IChainlinkOracle.sol";

contract ChainlinkOracle is IChainlinkOracle, Ownable2Step {
    uint256 private constant _MAX_TIMEOUT = 1 days;
    uint256 private constant _MIN_TIMEOUT = 20 minutes;
    uint256 private constant _MAX_GRACE_PERIOD = 1 days;
    uint256 private constant _MIN_GRACE_PERIOD = 20 minutes;

    uint256 public override timeout;
    address public override sequencerOracle;
    uint256 public override gracePeriod;
    address public override fallbackOracle;
    mapping(address => address[]) private _feeds;

    constructor(address sequencerOracle_, uint256 timeout_, uint256 gracePeriod_, address initialOwner)
        Ownable(initialOwner)
    {
        _setSequencerOracle(sequencerOracle_);
        _setTimeout(timeout_);
        _setGracePeriod(gracePeriod_);
    }

    function decimals() external pure returns (uint8) {
        return 8;
    }

    function getFeeds(address asset) external view returns (address[] memory) {
        return _feeds[asset];
    }

    function getAssetPrice(address asset) public view returns (uint256) {
        address[] memory feeds = _feeds[asset];
        if (feeds.length == 0) {
            return IOracle(fallbackOracle).getAssetPrice(asset);
        }
        uint256 price = 10 ** 8;
        for (uint256 i = 0; i < feeds.length; ++i) {
            try AggregatorV3Interface(feeds[i]).latestRoundData() returns (
                uint80 roundId, int256 answer, uint256, /* startedAt */ uint256 updatedAt, uint80 /* answeredInRound */
            ) {
                if (
                    roundId != 0 && answer >= 0 && updatedAt <= block.timestamp
                        && block.timestamp <= updatedAt + timeout && _isSequencerValid()
                ) {
                    uint256 feedDecimals = AggregatorV3Interface(feeds[i]).decimals();
                    price = price * uint256(answer) / 10 ** feedDecimals;
                    continue;
                }
            } catch {}
            return IOracle(fallbackOracle).getAssetPrice(asset);
        }
        return price;
    }

    function getAssetsPrices(address[] memory assets) external view returns (uint256[] memory prices) {
        prices = new uint256[](assets.length);
        unchecked {
            for (uint256 i = 0; i < assets.length; ++i) {
                prices[i] = getAssetPrice(assets[i]);
            }
        }
    }

    function isSequencerValid() external view returns (bool) {
        return _isSequencerValid();
    }

    function setFallbackOracle(address newFallbackOracle) external onlyOwner {
        fallbackOracle = newFallbackOracle;
        emit SetFallbackOracle(newFallbackOracle);
    }

    function setFeeds(address[] calldata assets, address[][] calldata feeds) external onlyOwner {
        if (assets.length != feeds.length) revert LengthMismatch();

        for (uint256 i = 0; i < assets.length; ++i) {
            if (feeds[i].length == 0) revert LengthMismatch();
            address[] storage _f = _feeds[assets[i]];
            assembly {
                sstore(_f.slot, 0)
            }
            for (uint256 j = 0; j < feeds[i].length; ++j) {
                _f.push(feeds[i][j]);
            }
            emit SetFeed(assets[i], feeds[i]);
        }
    }

    function setSequencerOracle(address newSequencerOracle) external onlyOwner {
        _setSequencerOracle(newSequencerOracle);
    }

    function _setSequencerOracle(address newSequencerOracle) internal {
        sequencerOracle = newSequencerOracle;
        emit SetSequencerOracle(newSequencerOracle);
    }

    function setTimeout(uint256 newTimeout) external onlyOwner {
        _setTimeout(newTimeout);
    }

    function _setTimeout(uint256 newTimeout) internal {
        if (newTimeout < _MIN_TIMEOUT || newTimeout > _MAX_TIMEOUT) revert InvalidTimeout();
        timeout = newTimeout;
        emit SetTimeout(newTimeout);
    }

    function setGracePeriod(uint256 newGracePeriod) external onlyOwner {
        _setGracePeriod(newGracePeriod);
    }

    function _setGracePeriod(uint256 newGracePeriod) internal {
        if (newGracePeriod < _MIN_GRACE_PERIOD || newGracePeriod > _MAX_GRACE_PERIOD) revert InvalidGracePeriod();
        gracePeriod = newGracePeriod;
        emit SetGracePeriod(newGracePeriod);
    }

    function _isSequencerValid() internal view returns (bool) {
        // @dev When the chain is L1, sequencerOracle is not set and always returns true.
        if (sequencerOracle == address(0)) {
            return true;
        }
        (, int256 answer,, uint256 updatedAt,) = AggregatorV3Interface(sequencerOracle).latestRoundData();
        return answer == 0 && block.timestamp - updatedAt > gracePeriod;
    }
}

src/oracle/DatastreamOracle.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later

pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {Ownable, Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";

import "../external/chainlink/ILogAutomation.sol";
import {Common} from "../external/chainlink/Common.sol";
import {IFeeManager} from "../external/chainlink/IFeeManager.sol";
import {IRewardManager} from "../external/chainlink/IRewardManager.sol";
import {IVerifierFeeManager} from "../external/chainlink/IVerifierFeeManager.sol";
import {IVerifierProxy} from "../external/chainlink/IVerifierProxy.sol";
import {StreamsLookupCompatibleInterface} from "../external/chainlink/StreamsLookupCompatibleInterface.sol";
import {IDatastreamOracle} from "../interfaces/IDatastreamOracle.sol";
import {IOracle} from "../interfaces/IOracle.sol";

contract DatastreamOracle is
    IDatastreamOracle,
    Ownable2Step,
    ILogAutomation,
    StreamsLookupCompatibleInterface,
    UUPSUpgradeable,
    Initializable
{
    string public constant STRING_DATASTREAMS_FEEDLABEL = "feedIDs";
    string public constant STRING_DATASTREAMS_QUERYLABEL = "timestamp";

    IVerifierProxy public immutable verifier;
    bytes32[] internal _feedIds;
    mapping(bytes32 => FeedData) public _feedData;
    mapping(address => uint256) internal _assetToPrice;
    address public fallbackOracle;
    address public forwarder;
    mapping(address => bool) public isOperator;
    uint256 public requestBitmap;

    modifier onlyOperator() {
        if (!isOperator[msg.sender]) revert NotOperator();
        _;
    }

    constructor(address verifier_) Ownable(msg.sender) {
        verifier = IVerifierProxy(verifier_);
    }

    function initialize(address owner_) external initializer {
        _transferOwnership(owner_);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    function feeToken() public view returns (address) {
        return IFeeManager(address(verifier.s_feeManager())).i_linkAddress();
    }

    function feeBalance() external view returns (uint256) {
        return IERC20(feeToken()).balanceOf(address(this));
    }

    function feedData(bytes32 feedId) external view returns (FeedData memory) {
        return _feedData[feedId];
    }

    function checkLog(Log calldata log, bytes memory) external view returns (bool, bytes memory) {
        uint256 length = _feedIds.length;
        string[] memory stringFeedIds = new string[](length);
        uint256 bitmap = requestBitmap;
        uint256 l = 0;
        for (uint256 i = 0; i < length; ++i) {
            if ((bitmap >> i) & 1 == 0) {
                continue;
            }
            stringFeedIds[l] = Strings.toHexString(uint256(_feedIds[i]), 32);
            ++l;
        }
        assembly {
            mstore(stringFeedIds, l)
        }
        revert StreamsLookup(
            STRING_DATASTREAMS_FEEDLABEL, stringFeedIds, STRING_DATASTREAMS_QUERYLABEL, log.timestamp, ""
        );
    }

    /**
     * @notice this is a new, optional function in streams lookup. It is meant to surface streams lookup errors.
     * @return upkeepNeeded boolean to indicate whether the keeper should call performUpkeep or not.
     * @return performData bytes that the keeper should call performUpkeep with, if
     * upkeep is needed. If you would like to encode data to decode later, try `abi.encode`.
     */
    function checkErrorHandler(uint256, /*errCode*/ bytes memory /*extraData*/ )
        external
        pure
        returns (bool, bytes memory)
    {
        return (true, "0");
        // Hardcoded to always perform upkeep.
        // Read the StreamsLookup error handler guide for more information.
        // https://docs.chain.link/chainlink-automation/guides/streams-lookup-error-handler
    }

    // The Data Streams report bytes is passed here.
    // extraData is context data from feed lookup process.
    // Your contract may include logic to further process this data.
    // This method is intended only to be simulated offchain by Automation.
    // The data returned will then be passed by Automation into performUpkeep
    function checkCallback(bytes[] calldata values, bytes calldata extraData)
        external
        pure
        returns (bool, bytes memory)
    {
        return (true, abi.encode(values, extraData));
    }

    function performUpkeep(bytes calldata performData) external {
        if (msg.sender != forwarder) revert InvalidForwarder();

        // Decode the performData bytes passed in by CL Automation.
        // This contains the data returned by your implementation in checkCallback().
        (bytes[] memory signedReports,) = abi.decode(performData, (bytes[], bytes));

        IFeeManager feeManager = IFeeManager(address(verifier.s_feeManager()));
        IRewardManager rewardManager = IRewardManager(address(feeManager.i_rewardManager()));

        address feeTokenAddress = feeManager.i_linkAddress();

        for (uint256 i = 0; i < signedReports.length; ++i) {
            bytes memory unverifiedReport = signedReports[i];

            (, /* bytes32[3] reportContextData */ bytes memory reportData) =
                abi.decode(unverifiedReport, (bytes32[3], bytes));

            // Report verification fees
            (Common.Asset memory fee,,) = feeManager.getFeeAndReward(address(this), reportData, feeTokenAddress);

            // Approve rewardManager to spend this contract's balance in fees
            if (fee.amount > 0) {
                IERC20(feeTokenAddress).approve(address(rewardManager), fee.amount);
            }

            // Verify the report
            bytes memory verifiedReportData = verifier.verify(unverifiedReport, abi.encode(feeTokenAddress));

            // Decode verified report data into a Report struct
            Report memory verifiedReport = abi.decode(verifiedReportData, (Report));

            address asset = _feedData[verifiedReport.feedId].asset;
            if (verifiedReport.price < 0) revert InvalidReport();
            _assetToPrice[asset] = uint256(uint192(verifiedReport.price));

            // Log price from report
            emit SetPrice(asset, uint256(uint192(verifiedReport.price)));
        }

        // Reset rewardManager's approval to spend this contract's balance in fees
        IERC20(feeTokenAddress).approve(address(rewardManager), 0);
    }

    function setFeed(bytes32 feedId, address asset) external onlyOwner {
        FeedData memory data = _feedData[feedId];
        data.asset = asset;
        if (data.index == 0) {
            data.index = uint96(_feedIds.length + 1);
            _feedIds.push(feedId);
        }
        _feedData[feedId] = data;
        emit SetFeed(data.asset, feedId, data.index);
    }

    function decimals() external pure returns (uint8) {
        return 18;
    }

    function getAssetPrice(address asset) public view returns (uint256 price) {
        price = _assetToPrice[asset];
        if (price == 0) {
            return IOracle(fallbackOracle).getAssetPrice(asset);
        }
    }

    function getAssetsPrices(address[] memory assets) external view returns (uint256[] memory prices) {
        prices = new uint256[](assets.length);
        unchecked {
            for (uint256 i = 0; i < assets.length; ++i) {
                prices[i] = getAssetPrice(assets[i]);
            }
        }
    }

    function setForwarder(address newForwarder) external onlyOwner {
        forwarder = newForwarder;
        emit SetForwarder(newForwarder);
    }

    function setOperator(address operator, bool status) external onlyOwner {
        isOperator[operator] = status;
        emit SetOperator(operator, status);
    }

    function request(uint256 bitmap) external onlyOperator {
        requestBitmap = bitmap;
        emit Request(msg.sender, bitmap);
    }

    function getFeedIds() external view returns (bytes32[] memory) {
        return _feedIds;
    }

    function getAllFeedData() external view returns (bytes32[] memory feedIds, FeedData[] memory data) {
        feedIds = _feedIds;
        uint256 length = feedIds.length;
        data = new FeedData[](length);
        for (uint256 i = 0; i < length; ++i) {
            data[i] = _feedData[feedIds[i]];
        }
    }

    function setFallbackOracle(address newFallbackOracle) external onlyOwner {
        fallbackOracle = newFallbackOracle;
        emit SetFallbackOracle(newFallbackOracle);
    }
}

src/Rebalancer.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later

pragma solidity ^0.8.0;

import {Ownable2Step, Ownable} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {IERC20, SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IBookManager} from "clober-dex/v2-core/interfaces/IBookManager.sol";
import {ILocker} from "clober-dex/v2-core/interfaces/ILocker.sol";
import {BookId, BookIdLibrary} from "clober-dex/v2-core/libraries/BookId.sol";
import {Currency, CurrencyLibrary} from "clober-dex/v2-core/libraries/Currency.sol";
import {OrderId, OrderIdLibrary} from "clober-dex/v2-core/libraries/OrderId.sol";
import {Tick, TickLibrary} from "clober-dex/v2-core/libraries/Tick.sol";
import {FeePolicy, FeePolicyLibrary} from "clober-dex/v2-core/libraries/FeePolicy.sol";
import {FixedPointMathLib} from "solmate/utils/FixedPointMathLib.sol";

import {IRebalancer} from "./interfaces/IRebalancer.sol";
import {IStrategy} from "./interfaces/IStrategy.sol";
import {ERC6909Supply} from "./libraries/ERC6909Supply.sol";

contract Rebalancer is IRebalancer, ILocker, Ownable2Step, ERC6909Supply {
    using BookIdLibrary for IBookManager.BookKey;
    using SafeERC20 for IERC20;
    using SafeCast for uint256;
    using CurrencyLibrary for Currency;
    using OrderIdLibrary for OrderId;
    using TickLibrary for Tick;
    using FeePolicyLibrary for FeePolicy;

    uint256 public constant RATE_PRECISION = 1e6;

    IBookManager public immutable bookManager;

    mapping(bytes32 key => Pool) private _pools;
    mapping(BookId => BookId) public bookPair;

    modifier selfOnly() {
        if (msg.sender != address(this)) revert NotSelf();
        _;
    }

    constructor(IBookManager bookManager_, address initialOwner_) Ownable(initialOwner_) {
        bookManager = bookManager_;
    }

    function decimals(uint256) external pure returns (uint8) {
        return 18;
    }

    function getPool(bytes32 key) external view returns (Pool memory) {
        return _pools[key];
    }

    function getBookPairs(bytes32 key) external view returns (BookId, BookId) {
        return (_pools[key].bookIdA, _pools[key].bookIdB);
    }

    function getLiquidity(bytes32 key) public view returns (Liquidity memory liquidityA, Liquidity memory liquidityB) {
        Pool storage pool = _pools[key];
        liquidityA.reserve = pool.reserveA;
        liquidityB.reserve = pool.reserveB;

        OrderId[] memory orderListA = pool.orderListA;
        OrderId[] memory orderListB = pool.orderListB;

        if (orderListA.length > 0) {
            IBookManager.BookKey memory bookKeyA = bookManager.getBookKey(pool.bookIdA);
            for (uint256 i; i < orderListA.length; ++i) {
                (uint256 cancelable, uint256 claimable) =
                    _getLiquidity(bookKeyA.makerPolicy, bookKeyA.unitSize, orderListA[i]);
                liquidityA.cancelable += cancelable;
                liquidityB.claimable += claimable;
            }
        }
        if (orderListB.length > 0) {
            IBookManager.BookKey memory bookKeyB = bookManager.getBookKey(pool.bookIdB);
            for (uint256 i; i < orderListB.length; ++i) {
                (uint256 cancelable, uint256 claimable) =
                    _getLiquidity(bookKeyB.makerPolicy, bookKeyB.unitSize, orderListB[i]);
                liquidityA.claimable += claimable;
                liquidityB.cancelable += cancelable;
            }
        }
    }

    function _getLiquidity(FeePolicy makerPolicy, uint64 unitSize, OrderId orderId)
        internal
        view
        returns (uint256 cancelable, uint256 claimable)
    {
        IBookManager.OrderInfo memory orderInfo = bookManager.getOrder(orderId);
        cancelable = uint256(orderInfo.open) * unitSize;
        claimable = orderId.getTick().quoteToBase(uint256(orderInfo.claimable) * unitSize, false);
        if (makerPolicy.usesQuote()) {
            int256 fee = makerPolicy.calculateFee(cancelable, true);
            cancelable = uint256(int256(cancelable) + fee);
        } else {
            int256 fee = makerPolicy.calculateFee(claimable, false);
            claimable = uint256(int256(claimable) - fee);
        }
    }

    function open(
        IBookManager.BookKey calldata bookKeyA,
        IBookManager.BookKey calldata bookKeyB,
        bytes32 salt,
        address strategy
    ) external returns (bytes32) {
        return abi.decode(
            bookManager.lock(
                address(this), abi.encodeWithSelector(this._open.selector, bookKeyA, bookKeyB, salt, strategy)
            ),
            (bytes32)
        );
    }

    function mint(bytes32 key, uint256 amountA, uint256 amountB, uint256 minLpAmount)
        external
        payable
        returns (uint256 mintAmount)
    {
        Pool storage pool = _pools[key];
        IBookManager.BookKey memory bookKeyA = bookManager.getBookKey(pool.bookIdA);

        uint256 supply = totalSupply[uint256(key)];
        if (supply == 0) {
            if (amountA == 0 || amountB == 0) revert InvalidAmount();
            // @dev If the decimals > 18, it will revert.
            uint256 complementA =
                bookKeyA.quote.isNative() ? 1 : 10 ** (18 - IERC20Metadata(Currency.unwrap(bookKeyA.quote)).decimals());
            uint256 complementB =
                bookKeyA.base.isNative() ? 1 : 10 ** (18 - IERC20Metadata(Currency.unwrap(bookKeyA.base)).decimals());
            uint256 _amountA = amountA * complementA;
            uint256 _amountB = amountB * complementB;
            mintAmount = _amountA > _amountB ? _amountA : _amountB;
        } else {
            (Liquidity memory liquidityA, Liquidity memory liquidityB) = getLiquidity(key);
            uint256 totalLiquidityA = liquidityA.reserve + liquidityA.claimable + liquidityA.cancelable;
            uint256 totalLiquidityB = liquidityB.reserve + liquidityB.claimable + liquidityB.cancelable;

            if (totalLiquidityA == 0 && totalLiquidityB == 0) {
                mintAmount = amountA = amountB = 0;
            } else if (totalLiquidityA == 0) {
                mintAmount = FixedPointMathLib.mulDivDown(amountB, supply, totalLiquidityB);
                amountA = 0;
            } else if (totalLiquidityB == 0) {
                mintAmount = FixedPointMathLib.mulDivDown(amountA, supply, totalLiquidityA);
                amountB = 0;
            } else {
                uint256 mintA = FixedPointMathLib.mulDivDown(amountA, supply, totalLiquidityA);
                uint256 mintB = FixedPointMathLib.mulDivDown(amountB, supply, totalLiquidityB);
                if (mintA > mintB) {
                    mintAmount = mintB;
                    amountA = FixedPointMathLib.mulDivUp(totalLiquidityA, mintAmount, supply);
                } else {
                    mintAmount = mintA;
                    amountB = FixedPointMathLib.mulDivUp(totalLiquidityB, mintAmount, supply);
                }
            }
        }
        if (mintAmount < minLpAmount) revert Slippage();

        uint256 refund = msg.value;
        if (bookKeyA.quote.isNative()) {
            if (msg.value < amountA) {
                revert InvalidValue();
            } else {
                unchecked {
                    refund -= amountA;
                }
            }
        } else {
            IERC20(Currency.unwrap(bookKeyA.quote)).safeTransferFrom(msg.sender, address(this), amountA);
        }
        if (bookKeyA.base.isNative()) {
            if (msg.value < amountB) {
                revert InvalidValue();
            } else {
                unchecked {
                    refund -= amountB;
                }
            }
        } else {
            IERC20(Currency.unwrap(bookKeyA.base)).safeTransferFrom(msg.sender, address(this), amountB);
        }

        pool.reserveA += amountA;
        pool.reserveB += amountB;

        _mint(msg.sender, uint256(key), mintAmount);
        pool.strategy.mintHook(msg.sender, key, mintAmount, supply);
        emit Mint(msg.sender, key, amountA, amountB, mintAmount);

        if (refund > 0) {
            CurrencyLibrary.NATIVE.transfer(msg.sender, refund);
        }
    }

    function burn(bytes32 key, uint256 amount, uint256 minAmountA, uint256 minAmountB)
        external
        returns (uint256 withdrawalA, uint256 withdrawalB)
    {
        (withdrawalA, withdrawalB) = abi.decode(
            bookManager.lock(address(this), abi.encodeWithSelector(this._burn.selector, key, msg.sender, amount)),
            (uint256, uint256)
        );
        if (withdrawalA < minAmountA || withdrawalB < minAmountB) revert Slippage();
    }

    function rebalance(bytes32 key) public {
        bookManager.lock(address(this), abi.encodeWithSelector(this._rebalance.selector, key));
    }

    function lockAcquired(address lockCaller, bytes calldata data) external returns (bytes memory) {
        if (msg.sender != address(bookManager)) revert InvalidLockAcquiredSender();
        if (lockCaller != address(this)) revert InvalidLockCaller();

        (bool success, bytes memory returnData) = address(this).call(data);
        if (success) return returnData;
        if (returnData.length == 0) revert LockFailure();
        // if the call failed, bubble up the reason
        /// @solidity memory-safe-assembly
        assembly {
            revert(add(returnData, 32), mload(returnData))
        }
    }

    function _open(
        IBookManager.BookKey calldata bookKeyA,
        IBookManager.BookKey calldata bookKeyB,
        bytes32 salt,
        address strategy
    ) public selfOnly returns (bytes32 key) {
        if (
            !(bookKeyA.quote.equals(bookKeyB.base) && bookKeyA.base.equals(bookKeyB.quote))
                || bookKeyA.quote.equals(bookKeyA.base)
        ) revert InvalidBookPair();
        if (address(bookKeyA.hooks) != address(0) || address(bookKeyB.hooks) != address(0)) revert InvalidHook();
        if (strategy == address(0)) revert InvalidStrategy();

        BookId bookIdA = bookKeyA.toId();
        BookId bookIdB = bookKeyB.toId();
        if (!bookManager.isOpened(bookIdA)) bookManager.open(bookKeyA, "");
        if (!bookManager.isOpened(bookIdB)) bookManager.open(bookKeyB, "");

        key = _encodeKey(bookIdA, bookIdB, salt);
        if (_pools[key].strategy != IStrategy(address(0))) revert AlreadyOpened();

        _pools[key].bookIdA = bookIdA;
        _pools[key].bookIdB = bookIdB;
        _pools[key].strategy = IStrategy(strategy);
        bookPair[bookIdA] = bookIdB;
        bookPair[bookIdB] = bookIdA;

        emit Open(key, bookIdA, bookIdB, salt, strategy);
    }

    function _burn(bytes32 key, address user, uint256 burnAmount)
        public
        selfOnly
        returns (uint256 withdrawalA, uint256 withdrawalB)
    {
        Pool storage pool = _pools[key];
        uint256 supply = totalSupply[uint256(key)];

        (uint256 canceledAmountA, uint256 canceledAmountB, uint256 claimedAmountA, uint256 claimedAmountB) =
            _clearPool(key, pool, burnAmount, supply);

        uint256 reserveA = pool.reserveA;
        uint256 reserveB = pool.reserveB;

        withdrawalA = (reserveA + claimedAmountA) * burnAmount / supply + canceledAmountA;
        withdrawalB = (reserveB + claimedAmountB) * burnAmount / supply + canceledAmountB;

        _burn(user, uint256(key), burnAmount);
        pool.strategy.burnHook(msg.sender, key, burnAmount, supply);
        emit Burn(user, key, withdrawalA, withdrawalB, burnAmount);

        IBookManager.BookKey memory bookKeyA = bookManager.getBookKey(pool.bookIdA);

        pool.reserveA = _settleCurrency(bookKeyA.quote, reserveA) - withdrawalA;
        pool.reserveB = _settleCurrency(bookKeyA.base, reserveB) - withdrawalB;

        if (withdrawalA > 0) {
            bookKeyA.quote.transfer(user, withdrawalA);
        }
        if (withdrawalB > 0) {
            bookKeyA.base.transfer(user, withdrawalB);
        }
    }

    function _rebalance(bytes32 key) public selfOnly {
        Pool storage pool = _pools[key];
        uint256 reserveA = pool.reserveA;
        uint256 reserveB = pool.reserveB;
        IBookManager.BookKey memory bookKeyA = bookManager.getBookKey(pool.bookIdA);
        IBookManager.BookKey memory bookKeyB = bookManager.getBookKey(pool.bookIdB);

        // Compute allocation
        try pool.strategy.computeOrders(key) returns (
            IStrategy.Order[] memory liquidityA, IStrategy.Order[] memory liquidityB
        ) {
            if (liquidityA.length == 0 && liquidityB.length == 0) return;
            _clearPool(key, pool, 1, 1);

            _setLiquidity(bookKeyA, liquidityA, pool.orderListA);
            _setLiquidity(bookKeyB, liquidityB, pool.orderListB);

            pool.strategy.rebalanceHook(msg.sender, key, liquidityA, liquidityB);
            emit Rebalance(key);
        } catch {
            _clearPool(key, pool, 1, 1);
        }

        pool.reserveA = _settleCurrency(bookKeyA.quote, reserveA);
        pool.reserveB = _settleCurrency(bookKeyA.base, reserveB);
    }

    function _clearPool(bytes32 key, Pool storage pool, uint256 cancelNumerator, uint256 cancelDenominator)
        internal
        returns (uint256 canceledAmountA, uint256 canceledAmountB, uint256 claimedAmountA, uint256 claimedAmountB)
    {
        (canceledAmountA, claimedAmountB) = _clearOrders(pool.orderListA, cancelNumerator, cancelDenominator);
        (canceledAmountB, claimedAmountA) = _clearOrders(pool.orderListB, cancelNumerator, cancelDenominator);
        emit Claim(key, claimedAmountA, claimedAmountB);
        emit Cancel(key, canceledAmountA, canceledAmountB);
    }

    function _clearOrders(OrderId[] storage orderIds, uint256 cancelNumerator, uint256 cancelDenominator)
        internal
        returns (uint256 canceledAmount, uint256 claimedAmount)
    {
        OrderId[] memory mOrderIds = orderIds;
        for (uint256 i = 0; i < mOrderIds.length; ++i) {
            OrderId orderId = mOrderIds[i];
            IBookManager.OrderInfo memory orderInfo = bookManager.getOrder(orderId);
            if (orderInfo.claimable > 0) {
                claimedAmount += bookManager.claim(orderId, "");
            }
            if (orderInfo.open > 0) {
                canceledAmount += bookManager.cancel(
                    IBookManager.CancelParams({
                        id: orderId,
                        toUnit: (orderInfo.open - orderInfo.open * cancelNumerator / cancelDenominator).toUint64()
                    }),
                    ""
                );
            }
        }
        if (cancelDenominator == cancelNumerator) {
            assembly {
                sstore(orderIds.slot, 0)
            }
        }
    }

    function _setLiquidity(
        IBookManager.BookKey memory bookKey,
        IStrategy.Order[] memory liquidity,
        OrderId[] storage emptyOrderIds
    ) internal {
        for (uint256 i = 0; i < liquidity.length; ++i) {
            if (liquidity[i].rawAmount == 0) continue;
            (OrderId orderId,) = bookManager.make(
                IBookManager.MakeParams({
                    key: bookKey,
                    tick: liquidity[i].tick,
                    unit: liquidity[i].rawAmount,
                    provider: address(0)
                }),
                ""
            );
            emptyOrderIds.push(orderId);
        }
    }

    function _settleCurrency(Currency currency, uint256 liquidity) internal returns (uint256) {
        bookManager.settle(currency);

        int256 delta = bookManager.getCurrencyDelta(address(this), currency);
        if (delta > 0) {
            bookManager.withdraw(currency, address(this), uint256(delta));
            liquidity += uint256(delta);
        } else if (delta < 0) {
            currency.transfer(address(bookManager), uint256(-delta));
            bookManager.settle(currency);
            liquidity -= uint256(-delta);
        }
        return liquidity;
    }

    function _encodeKey(BookId bookIdA, BookId bookIdB, bytes32 salt) internal pure returns (bytes32) {
        if (BookId.unwrap(bookIdA) > BookId.unwrap(bookIdB)) (bookIdA, bookIdB) = (bookIdB, bookIdA);
        return keccak256(abi.encodePacked(bookIdA, bookIdB, salt));
    }

    receive() external payable {}
}

src/SimpleOracleStrategy.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later

pragma solidity ^0.8.0;

import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable, Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {Tick, TickLibrary} from "clober-dex/v2-core/libraries/Tick.sol";
import {IBookManager} from "clober-dex/v2-core/interfaces/IBookManager.sol";
import {FeePolicy, FeePolicyLibrary} from "clober-dex/v2-core/libraries/FeePolicy.sol";
import {BookId} from "clober-dex/v2-core/libraries/BookId.sol";
import {Currency, CurrencyLibrary} from "clober-dex/v2-core/libraries/Currency.sol";

import {IStrategy} from "./interfaces/IStrategy.sol";
import {IOracle} from "./interfaces/IOracle.sol";
import {ISimpleOracleStrategy} from "./interfaces/ISimpleOracleStrategy.sol";
import {IRebalancer} from "./interfaces/IRebalancer.sol";

contract SimpleOracleStrategy is ISimpleOracleStrategy, Ownable2Step {
    using CurrencyLibrary for Currency;
    using FeePolicyLibrary for FeePolicy;
    using TickLibrary for Tick;

    uint256 public constant RATE_PRECISION = 1e6;
    uint256 public constant LAST_RAW_AMOUNT_MASK = (1 << 128) - 1;

    IOracle public immutable referenceOracle;
    IRebalancer public immutable rebalancer;
    IBookManager public immutable bookManager;

    mapping(address => bool) public isOperator;
    mapping(bytes32 => Config) internal _configs;
    mapping(bytes32 => Position) internal _positions;
    mapping(bytes32 => uint256) internal _lastRawAmounts;

    modifier onlyOperator() {
        if (!isOperator[msg.sender]) revert NotOperator();
        _;
    }

    constructor(IOracle referenceOracle_, IRebalancer rebalancer_, IBookManager bookManager_, address initialOwner)
        Ownable(initialOwner)
    {
        referenceOracle = referenceOracle_;
        rebalancer = rebalancer_;
        bookManager = bookManager_;
    }

    function getConfig(bytes32 key) external view returns (Config memory) {
        return _configs[key];
    }

    function getPosition(bytes32 key) external view returns (Position memory) {
        return _positions[key];
    }

    function getLastRawAmount(bytes32 key) external view returns (uint256, uint256) {
        uint256 lastRawAmounts = _lastRawAmounts[key];
        return (lastRawAmounts >> 128, lastRawAmounts & LAST_RAW_AMOUNT_MASK);
    }

    function computeOrders(bytes32 key) external view returns (Order[] memory ordersA, Order[] memory ordersB) {
        Position memory position = _positions[key];
        if (position.paused) revert Paused();

        Config memory config = _configs[key];

        IBookManager.BookKey memory bookKeyA;
        IBookManager.BookKey memory bookKeyB;
        IRebalancer.Liquidity memory liquidityA;
        IRebalancer.Liquidity memory liquidityB;
        {
            (BookId bookIdA, BookId bookIdB) = rebalancer.getBookPairs(key);
            bookKeyA = bookManager.getBookKey(bookIdA);
            bookKeyB = bookManager.getBookKey(bookIdB);

            (liquidityA, liquidityB) = rebalancer.getLiquidity(key);

            uint256 lastRawAmounts = _lastRawAmounts[key];
            if (
                lastRawAmounts > 0
                    && (
                        liquidityA.cancelable
                            > (lastRawAmounts >> 128) * bookKeyA.unitSize * config.rebalanceThreshold / RATE_PRECISION
                            || liquidityB.cancelable
                                > (lastRawAmounts & LAST_RAW_AMOUNT_MASK) * bookKeyB.unitSize * config.rebalanceThreshold
                                    / RATE_PRECISION
                    )
            ) {
                return (ordersA, ordersB);
            }

            if (!_isOraclePriceValid(position.oraclePrice, config.referenceThreshold, bookKeyA.quote, bookKeyA.base)) {
                revert InvalidOraclePrice();
            }
        }

        (uint256 amountA, uint256 amountB) = _calculateAmounts(
            liquidityA.reserve + liquidityA.cancelable + liquidityA.claimable,
            liquidityB.reserve + liquidityB.cancelable + liquidityB.claimable,
            position.oraclePrice,
            _getCurrencyDecimals(bookKeyA.quote),
            _getCurrencyDecimals(bookKeyA.base),
            config
        );

        if (bookKeyA.makerPolicy.usesQuote()) amountA = bookKeyA.makerPolicy.calculateOriginalAmount(amountA, false);
        if (bookKeyB.makerPolicy.usesQuote()) amountB = bookKeyB.makerPolicy.calculateOriginalAmount(amountB, false);

        // SimpleStrategy has only one bid and one ask order
        ordersA = new Order[](1);
        ordersB = new Order[](1);
        ordersA[0] = Order({
            tick: position.tickA,
            rawAmount: SafeCast.toUint64(amountA * position.rate / bookKeyA.unitSize / RATE_PRECISION)
        });
        ordersB[0] = Order({
            tick: position.tickB,
            rawAmount: SafeCast.toUint64(amountB * position.rate / bookKeyB.unitSize / RATE_PRECISION)
        });

        return (ordersA, ordersB);
    }

    function _calculateAmounts(
        uint256 amountA,
        uint256 amountB,
        uint256 oraclePrice,
        uint8 decimalsA,
        uint8 decimalsB,
        Config memory config
    ) internal view returns (uint256 resultA, uint256 resultB) {
        // @dev Use the same decimals for both amounts to calculate the value properly
        if (decimalsA > decimalsB) {
            amountB = amountB * 10 ** (decimalsA - decimalsB);
        } else if (decimalsA < decimalsB) {
            amountA = amountA * 10 ** (decimalsB - decimalsA);
        }

        resultA = amountA * config.rateA / RATE_PRECISION;
        resultB = amountB * config.rateB / RATE_PRECISION;

        uint256 basePrice = 10 ** referenceOracle.decimals();
        uint256 valueA = resultA * basePrice;
        uint256 valueB = resultB * oraclePrice;

        if (valueA > valueB) {
            resultA = valueB / basePrice;
            valueA = resultA * basePrice;
        } else {
            resultB = valueA / oraclePrice;
            valueB = resultB * oraclePrice;
        }

        if (valueA < amountA * config.minRateA / RATE_PRECISION * basePrice) {
            resultA = amountA * config.minRateA / RATE_PRECISION;
        }
        if (valueB < amountB * config.minRateB / RATE_PRECISION * oraclePrice) {
            resultB = amountB * config.minRateB / RATE_PRECISION;
        }

        // @dev Turn back to original decimals
        if (decimalsA > decimalsB) {
            resultB = resultB / 10 ** (decimalsA - decimalsB);
        } else if (decimalsA < decimalsB) {
            resultA = resultA / 10 ** (decimalsB - decimalsA);
        }
    }

    function isOraclePriceValid(bytes32 key) external view returns (bool) {
        Config memory config = _configs[key];
        Position memory position = _positions[key];

        (BookId bookIdA,) = rebalancer.getBookPairs(key);

        IBookManager.BookKey memory bookKeyA = bookManager.getBookKey(bookIdA);

        return _isOraclePriceValid(position.oraclePrice, config.referenceThreshold, bookKeyA.quote, bookKeyA.base);
    }

    function _isOraclePriceValid(
        uint256 oraclePrice,
        uint256 referenceThreshold,
        Currency currencyA,
        Currency currencyB
    ) internal view returns (bool) {
        uint256 referencePrice;
        address[] memory assets = new address[](2);
        assets[0] = Currency.unwrap(currencyA);
        assets[1] = Currency.unwrap(currencyB);

        try referenceOracle.getAssetsPrices(assets) returns (uint256[] memory prices) {
            // price = basePrice / quotePrice
            referencePrice = prices[1] * 10 ** referenceOracle.decimals() / prices[0];
        } catch {
            return false;
        }

        if (
            referencePrice * (RATE_PRECISION + referenceThreshold) / RATE_PRECISION < oraclePrice
                || referencePrice * (RATE_PRECISION - referenceThreshold) / RATE_PRECISION > oraclePrice
        ) {
            return false;
        }
        return true;
    }

    function isPaused(bytes32 key) external view returns (bool) {
        return _positions[key].paused;
    }

    function pause(bytes32 key) external onlyOperator {
        delete _lastRawAmounts[key];
        _positions[key].paused = true;
        emit Pause(key);
    }

    function unpause(bytes32 key) external onlyOperator {
        _positions[key].paused = false;
        emit Unpause(key);
    }

    function updatePosition(bytes32 key, uint256 oraclePrice, Tick tickA, Tick tickB, uint24 rate)
        external
        onlyOperator
    {
        uint256 priceA = tickA.toPrice();
        uint256 priceB = Tick.wrap(-Tick.unwrap(tickB)).toPrice();

        (BookId bookIdA, BookId bookIdB) = rebalancer.getBookPairs(key);
        IBookManager.BookKey memory bookKeyA = bookManager.getBookKey(bookIdA);

        uint256 priceWithFee = uint256(int256(priceA) - bookKeyA.makerPolicy.calculateFee(priceA, false));
        priceWithFee = uint256(
            int256(priceWithFee) - bookManager.getBookKey(bookIdB).makerPolicy.calculateFee(priceWithFee, false)
        );

        if (priceWithFee >= priceB) revert InvalidPrice();
        if (rate > RATE_PRECISION) revert InvalidValue();

        Config memory config = _configs[key];
        if (
            oraclePrice * (RATE_PRECISION + config.priceThresholdA) / RATE_PRECISION < priceA
                || oraclePrice * (RATE_PRECISION - config.priceThresholdB) / RATE_PRECISION > priceB
        ) revert ExceedsThreshold();

        // @dev Convert oracle price to the same decimals as the reference oracle
        oraclePrice =
            oraclePrice * 10 ** _getCurrencyDecimals(bookKeyA.base) / 10 ** _getCurrencyDecimals(bookKeyA.quote);
        oraclePrice = (oraclePrice * 10 ** referenceOracle.decimals()) >> 96;
        if (!_isOraclePriceValid(oraclePrice, config.referenceThreshold, bookKeyA.quote, bookKeyA.base)) {
            revert InvalidOraclePrice();
        }

        Position memory position = _positions[key];
        position.oraclePrice = SafeCast.toUint128(oraclePrice);
        position.tickA = tickA;
        position.tickB = tickB;
        position.rate = rate;

        _positions[key] = position;
        delete _lastRawAmounts[key];
        emit UpdatePosition(key, oraclePrice, tickA, tickB, rate);
    }

    function setConfig(bytes32 key, Config memory config) external onlyOwner {
        if (
            config.referenceThreshold > RATE_PRECISION || config.rebalanceThreshold > RATE_PRECISION
                || config.rateA > RATE_PRECISION || config.rateB > RATE_PRECISION || config.minRateA > RATE_PRECISION
                || config.minRateB > RATE_PRECISION || config.priceThresholdA > RATE_PRECISION
                || config.priceThresholdB > RATE_PRECISION
        ) revert InvalidConfig();

        if (config.rateA < config.minRateA || config.rateB < config.minRateB) revert InvalidConfig();

        _configs[key] = config;
        emit UpdateConfig(key, config);
    }

    function setOperator(address operator, bool status) external onlyOwner {
        isOperator[operator] = status;
        emit SetOperator(operator, status);
    }

    function _getCurrencyDecimals(Currency currency) internal view returns (uint8) {
        return currency.isNative() ? 18 : IERC20Metadata(Currency.unwrap(currency)).decimals();
    }

    function mintHook(address, bytes32, uint256, uint256) external view {
        if (msg.sender != address(rebalancer)) revert InvalidAccess();
    }

    function burnHook(address, bytes32 key, uint256 burnAmount, uint256 lastTotalSupply) external {
        if (msg.sender != address(rebalancer)) revert InvalidAccess();
        uint256 lastRawAmounts = _lastRawAmounts[key];
        _lastRawAmounts[key] = lastRawAmounts - (((lastRawAmounts >> 128) * burnAmount / lastTotalSupply) << 128)
            - (lastRawAmounts & LAST_RAW_AMOUNT_MASK) * burnAmount / lastTotalSupply;
    }

    function rebalanceHook(address, bytes32 key, Order[] memory liquidityA, Order[] memory liquidityB) external {
        if (msg.sender != address(rebalancer)) revert InvalidAccess();
        uint256 lastRawAmountA;
        uint256 lastRawAmountB;
        for (uint256 i = 0; i < liquidityA.length; ++i) {
            IStrategy.Order memory order = liquidityA[i];
            lastRawAmountA += order.rawAmount;
        }

        for (uint256 i = 0; i < liquidityB.length; ++i) {
            IStrategy.Order memory order = liquidityB[i];
            lastRawAmountB += order.rawAmount;
        }
        _lastRawAmounts[key] = (lastRawAmountA << 128) + lastRawAmountB;
    }
}
